<?phpif(!isset($_GET['id'])) Site::notFound();$id = App::filter('int', $_GET['id']);$db = App::db();if($db->query("SELECT * FROM `forum_pt` WHERE `id` = '$id'")->rowCount() == 0) Site::notFound();$post = $db->query("SELECT * FROM `forum_pt` WHERE `id` = '$id'")->fetch();Site::header($db->query("SELECT name FROM `forum_t` WHERE `id` = '".$post['topic_id']."'")->fetchColumn().' - '. _t('Forum', 'forum'));echo Site::div('title', Site::breadcrumbs($db->query("SELECT name FROM `forum_t` WHERE `id` = '".$post['topic_id']."'")->fetchColumn()));echo '<div class="content">'. user()->nick($post['user_id'], '<small>'. App::date($post['time']) .'</small>') .''. Site::output($post['text']) .'</div>';echo '<div class="action_list">'. (user()->level() >=3 || (user()->getId() == $post['user_id'] && $post['time'] > (time()-300)) ?' <a href="/forum/delete_post/'.$post['id'].'?topic_id='.$post['topic_id'].'">'.Site::icon('arrow-right').' '. _t('Delete') .'</a>'.' <a href="/forum/edit_post/'.$post['id'].'?topic_id='.$post['topic_id'].'">'.Site::icon('arrow-right').' '. _t('Edit').'</a>' : NULL).' <a href="/forum/topic/'.$post['topic_id'].'?page=end">'.Site::icon('arrow-left').' '. _t('Back').'</a></div>';Site::footer();